import React, { useState } from 'react';
import axios from 'axios';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/auth/login`, {
        email,
        password
      });

      localStorage.setItem('token', res.data.token);
      window.location.href = '/dashboard';
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center bg-[#111714]"
      style={{ fontFamily: '"Spline Sans", "Noto Sans", sans-serif' }}
    >
      <div className="w-full max-w-md p-6 bg-[#1c2620] rounded-2xl shadow-lg">
        <h2 className="text-white text-2xl font-bold text-center mb-6">
          Welcome Back
        </h2>

        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block text-white mb-2">Email</label>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full rounded-lg border border-[#3d5245] bg-[#111714] text-white px-3 py-2 focus:outline-none focus:ring-2 focus:ring-[#38e07b] placeholder-[#9eb7a8]"
              required
            />
          </div>

          <div>
            <label className="block text-white mb-2">Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full rounded-lg border border-[#3d5245] bg-[#111714] text-white px-3 py-2 focus:outline-none focus:ring-2 focus:ring-[#38e07b] placeholder-[#9eb7a8]"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-[#38e07b] text-[#111714] font-bold py-2 rounded-full hover:bg-green-500 transition duration-300"
          >
            Log In
          </button>

          {error && (
            <p className="text-red-500 text-sm text-center mt-2">{error}</p>
          )}
        </form>

        <p className="text-[#9eb7a8] text-sm text-center mt-4 underline cursor-pointer">
          Forgot password?
        </p>
      </div>

      <footer className="text-center mt-8 text-[#9eb7a8] text-sm">
        Â©2024 Teacher Portal. All rights reserved.
      </footer>
    </div>
  );
}
