import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [teacherName, setTeacherName] = useState("");
  const [classes, setClasses] = useState([]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return window.location.href = "/login";

    axios
      .get(`${process.env.REACT_APP_API_URL}/dashboard`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setTeacherName(res.data.teacherName);
        setClasses(res.data.classes);
      })
      .catch(() => window.location.href = "/login");
  }, []);

  return (
    <div className="min-h-screen bg-[#111714] font-sans flex flex-col">
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-[#111714] border-b border-[#0f6d3d]">
        <h2 className="text-white text-lg font-bold">Dashboard</h2>
        <button
          onClick={handleLogout}
          className="bg-[#38e07b] text-[#111714] px-4 py-2 rounded hover:bg-[#22ff88] transition"
        >
          Logout
        </button>
      </div>

      {/* Welcome */}
      <div className="px-6 py-4">
        <h2 className="text-white text-2xl font-bold pb-2">
          Hello, {teacherName}
        </h2>
        <h3 className="text-green-400 text-xl font-semibold">My Classes</h3>
      </div>

      {/* Classes List */}
      <div className="px-6 space-y-3 flex-1">
        {classes.length === 0 ? (
          <p className="text-white">No classes assigned yet.</p>
        ) : (
          classes.map((cls) => (
            <div
              key={cls.id}
              className="flex items-center gap-4 bg-[#29382f] px-4 py-3 rounded-lg cursor-pointer hover:bg-[#0f6d3d] transition"
              onClick={() => window.location.href = `/class/${cls.id}`}
            >
              <div className="text-white flex items-center justify-center rounded-full w-12 h-12 bg-[#0f6d3d]">
                {/* Book Icon */}
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  fill="currentColor"
                  viewBox="0 0 256 256"
                >
                  <path d="M224,48H160a40,40,0,0,0-32,16A40,40,0,0,0,96,48H32A16,16,0,0,0,16,64V192a16,16,0,0,0,16,16H96a24,24,0,0,1,24,24,8,8,0,0,0,16,0,24,24,0,0,1,24-24h64a16,16,0,0,0,16-16V64A16,16,0,0,0,224,48ZM96,192H32V64H96a24,24,0,0,1,24,24V200A39.81,39.81,0,0,0,96,192Zm128,0H160a39.81,39.81,0,0,0-24,8V88a24,24,0,0,1,24-24h64Z"></path>
                </svg>
              </div>
              <div className="flex flex-col justify-center">
                <p className="text-white font-medium">{cls.department}</p>
                <p className="text-green-400 font-normal">{cls.section}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
