// src/pages/Dashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [teacherName, setTeacherName] = useState("");
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      window.location.href = "/login";
      return;
    }

    const fetchDashboard = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/dashboard`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTeacherName(res.data.teacherName);
        setClasses(res.data.classes);
        setLoading(false);
      } catch (err) {
        console.error(err);
        localStorage.removeItem("token");
        window.location.href = "/login";
      }
    };

    fetchDashboard();
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center text-white">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-[#111714] font-sans text-white">
      <header className="flex justify-between items-center p-4 border-b border-green-800">
        <h1 className="text-2xl font-bold">Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-green-500 hover:bg-green-600 px-4 py-2 rounded font-bold"
        >
          Logout
        </button>
      </header>

      <main className="p-4">
        <h2 className="text-xl font-semibold mb-4">Hello, {teacherName}</h2>
        <h3 className="text-lg font-semibold mb-2">My Classes</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {classes.length === 0 && <p>No classes assigned yet.</p>}
          {classes.map((cls) => (
            <div
              key={cls.id}
              className="bg-[#29382f] p-4 rounded-lg cursor-pointer hover:bg-green-700 transition"
              onClick={() => window.location.href = `/class/${cls.id}`}
            >
              <p className="font-bold text-lg">{cls.department}</p>
              <p className="text-green-300">Section {cls.section}</p>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
